import axios from "axios";
import s from "./Finder.module.css";

const Finder = (props) => {

    if(props.users.length === 0){

      axios.get('https://social-network.samuraijs.com/api/1.0/users').then(response => {

      })

    }

    return (
    <div className={s.finder_wrapper}>
      <div className={s.search_block}>
        <input type="text" placeholder="Введите ФИО друга" />
        <i className="material-icons">search</i>
      </div>
      <div className={s.users}>
        {props.users.map((user) => {
          return (
            <div className={s.user_block} key={user.userId}>
              <div className={s.user_ava}>
                <span className={s.ava}></span>
              </div>
              <div className={s.user_name}>
                {user.name} {user.surname}
              </div>
              <div className={s.user_status}>
                Status: <br /> {user.status}
              </div>
              <div className={s.user_location}>
                <i className="material-icons">location_on</i>
                {"Location: " +
                  user.location.country +
                  ", " +
                  user.location.city}
              </div>
              <div className={s.user_button}><button className={user.follow ? s.followed : s.unfollowed} onClick={()=>{props.follow(user.userId);}}>{user.follow ? 'Unfollow' : 'Follow'}</button></div>
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default Finder;
