import Dialogs from "./Dialogs";
import { UpdateMessageTextActionCreator, SendMessageActionCreator } from "../../../redux/reducers/dialogs-reducer";
import StoreContext from '../../../StoreContext';

const DialogsConteiner = (props) => {
    let state = props.store.getState()

    let SendMessage = () =>{
        let action = SendMessageActionCreator();
        props.store.dispatch(action);
    }

    let UpdateEnteredText = (text) =>{
        let action = UpdateMessageTextActionCreator(text);
        props.store.dispatch(action);
    }

    return ( 

        <StoreContext.Consumer>{
            (store) => {
    return <Dialogs send_message={SendMessage} update_entered_text={UpdateEnteredText} data={state.DialogsPage}/> 
        }}
    </StoreContext.Consumer>
    )
}

export default DialogsConteiner;
