import { connect } from 'react-redux';
import { getUsersThunk , follow, expandPageThunk } from '../../../redux/reducers/finder-reducer';
import React from "react";
import Finder from "./Finder";

class FinderContainer extends React.Component {

    componentDidMount(){
        this.props.getUsersThunk(this.props.pageSize);
    }

    onPageChanged = (pageSize) => {
      this.props.expandPageThunk(this.props.pageSize);
    }

    render() {
      return (
      <>
          <Finder
            follow={this.props.follow}
            onPageChanged={this.onPageChanged}
            pageSize={this.props.pageSize}
            users={this.props.users}
          />
      </>
    );
}
  }

let mapStateToProps = (state) =>{
    return{
        users: state.FinderPage.users,
        totalUsersCount: state.FinderPage.totalUsersCount,
        pageSize: state.FinderPage.pageSize,
    }
} 


export default connect(mapStateToProps, {follow, getUsersThunk, expandPageThunk})(FinderContainer);
