const ADD_POST = 'ADD-POST';
const UPDATE_POST_TEXT = 'UPDATE-POST-TEXT';
const UPDATE_MESSAGE_TEXT = 'UPDATE-MESSAGE-TEXT';
const SEND_MESSAGE = 'SEND-MESSAGE';

let store = {

   _state: {

    ProfilePage: {
      posts: [
        { id: 1, text: "Hey, im here!", likescount: 20 },
        { id: 2, text: "Hello world!", likescount: 10 },
        { id: 3, text: "Hello!", likescount: 10 },
        { id: 4, text: "world!", likescount: 10 },
      ] ,
      EnteredText : 'i got this'
    },
  
    DialogsPage: {
      messages: [
        { id: 1, text: "Hi!" },
        { id: 2, text: "Yo!" },
        { id: 3, text: "Lol!" },
        { id: 4, text: "Kek" },
        { id: 5, text: "Chevurek!" },
      ],

      NewMessageText: '',

      dialogs: [
        { id: 1, name: "Alex" },
        { id: 2, name: "Vasya" },
        { id: 3, name: "Petya" },
        { id: 4, name: "Kolya" },
        { id: 5, name: "Ivan" },
      ],
    },
    
    NavMenu: {
      links: [
        {id: 1, href:'/Profile', name:'Profile', icon:'face'},
        {id: 2, href:'/Dialogs', name:'Dialogs', icon:'mail'},
        {id: 3, href:'/Friends', name:'Friends', icon:'audiotrack'}
      ],
      logo: ['Logo'],
    },
  
    FriendsPage:{
      friends: [
        { id: 1, name: "Alex", photo: "" },
        { id: 2, name: "Vasya", photo: ""},
        { id: 3, name: "Petya", photo: ""},
        { id: 4, name: "Kolya", photo: "" },
        { id: 5, name: "Ivan", photo: "" }
      ]
    },
  },
  _callSubscriber() {
    console.log('State changed');
  },

  _AddPost(){
    let newPost = {
      id: 5,
      text: this._state.ProfilePage.EnteredText,
      likescount: 10,
    };
    this._state.ProfilePage.posts.push(newPost);
    this._state.ProfilePage.EnteredText = '';
    this._callSubscriber(this._state);
  },
  _UpdatePostText(newText){
    this._state.ProfilePage.EnteredText = newText;
    this._callSubscriber(this._state);
  },
  _UpdateMessageText(newText){
    this._state.DialogsPage.NewMessageText = newText;
    this._callSubscriber(this._state);
  },
  _SendMessage(){
    let text = this._state.DialogsPage.NewMessageText;
    alert(text);
    this._state.DialogsPage.NewMessageText = '';
    this._state.DialogsPage.messages.push({id: 6, message: text});
    this._callSubscriber(this._state);
  },

  getState() {
    return this._state;
  },
  subscribe(observer){
    this._callSubscriber = observer;
  },

  dispatch(action){ // {type: 'ADD-POST' any: '' }
    if(action.type === ADD_POST){
      this._AddPost();
    }
    else if(action.type === UPDATE_POST_TEXT){
      this._UpdatePostText(action.newText);
    }
    else if(action.type === UPDATE_MESSAGE_TEXT){
      this._UpdateMessageText(action.newText);
    }
    else if(action.type === SEND_MESSAGE){
      this._SendMessage();
    }
  }

}

export const AddPostActionCreator = () => ({type: ADD_POST})
export const OnTextEnterActionCreator = (text) => ({type: UPDATE_POST_TEXT , newText: text})

export const SendMessageActionCreator = () => ({type: SEND_MESSAGE})
export const UpdateMessageTextActionCreator = (text) => ({type: UPDATE_MESSAGE_TEXT, newText: text})

window.store = store;

export default store;
