let rerenderEntireTree = () =>{

}

let state = {

  ProfilePage: {
    posts: [
      { id: 1, text: "Hey, im here!", likescount: 20 },
      { id: 2, text: "Hello world!", likescount: 10 },
      { id: 3, text: "Hello!", likescount: 10 },
      { id: 4, text: "world!", likescount: 10 },
    ] ,
    EnteredText : 'i got this'
  },

  DialogsPage: {
    messages: [
      { id: 1, text: "Hi!" },
      { id: 2, text: "Yo!" },
      { id: 3, text: "Lol!" },
      { id: 4, text: "Kek" },
      { id: 5, text: "Chevurek!" },
    ],
    dialogs: [
      { id: 1, name: "Alex" },
      { id: 2, name: "Vasya" },
      { id: 3, name: "Petya" },
      { id: 4, name: "Kolya" },
      { id: 5, name: "Ivan" },
    ],
  },
  
  NavMenu: {
    links: [
      {id: 1, href:'/Profile', name:'Profile', icon:'face'},
      {id: 2, href:'/Dialogs', name:'Dialogs', icon:'mail'},
      {id: 3, href:'/Friends', name:'Friends', icon:'audiotrack'}
    ],
    logo: ['Logo'],
  },

  FriendsPage:{
    friends: [
      { id: 1, name: "Alex", photo: "" },
      { id: 2, name: "Vasya", photo: ""},
      { id: 3, name: "Petya", photo: ""},
      { id: 4, name: "Kolya", photo: "" },
      { id: 5, name: "Ivan", photo: "" }
    ]
  },
};

window.state = state;

export let AddPost = () => {
  let newPost = {
    id: 5,
    text: state.ProfilePage.EnteredText,
    likescount: 10,
  };
  state.ProfilePage.posts.push(newPost);
  state.ProfilePage.EnteredText = '';
  rerenderEntireTree();
}

export let updatePostText = (newText) => {
  state.ProfilePage.EnteredText = newText;
  rerenderEntireTree();
}

export const subscribe = (observer) =>{
  rerenderEntireTree = observer;
}
export default state;
