import s from "./Finder.module.css";
import Preloader from '../../common/Preloader/Preloader';
import { NavLink } from 'react-router-dom';
import axios from "axios";

const Finder = (props) => {
    return (
      <div className={s.finder_wrapper}>
        <div className={s.search_block}>
          <input type="text" placeholder="Введите ФИО друга" />
          <i className="material-icons">search</i>
        </div>
        <div className={s.users}>
          {props.users.map((user) => (
            <div className={s.user_block} key={user.id}>
              <NavLink className={s.user_ava} to={`/Profile/${user.id}`}>
                {user.photos.small ? (
                  <img src={user.photos.small} style={{ padding: 10 + "px" }} />
                ) : (
                  <span className={s.ava}></span>
                )}
              </NavLink>
              <div className={s.user_name}>{user.name}</div>
              <div className={s.user_status}>
                Status: <br /> {user.status}
              </div>
              <div className={s.user_location}>
                <i className="material-icons">location_on</i>
                {"Location: " +
                  "user.location.country" +
                  ", " +
                  "user.location.city"}
              </div>
              <div className={s.user_button}>
                <button
                  className={user.follow ? s.followed : s.unfollowed}
                  onClick={() => {
                    if(user.follow){
                      axios.delete(`https://social-network.samuraijs.com/api/1.0/follow/${user.id}`, {}, {withCredentials: true,
                      headers: {'API-KEY': '0ab0bc90-b21b-4863-9010-048b6877ad81'}})
                    .then((resp)=>{
                      if(resp.data.resultCode===0){
                        props.follow(user.id);
                      }
                    })
                    }
                    else{
                      axios.post(`https://social-network.samuraijs.com/api/1.0/follow/${user.id}`, {}, {withCredentials: true,
                      headers: {'API-KEY': '0ab0bc90-b21b-4863-9010-048b6877ad81'}})
                    .then((resp)=>{
                      if(resp.data.resultCode===0){
                        props.follow(user.id);
                      }
                    })
                    }
                  }}
                >
                  {user.follow ? "Unfollow" : "Follow"}
                </button>
              </div>
            </div>
          ))}
          {props.isFetching ? <Preloader /> : null}
        </div>
        <div className={s.page_counter_block}>
          <button
            onClick={() => {
              props.onPageChanged(props.pageSize + 5);
            }}
          >
            More users
          </button>
        </div>
      </div>
    );
}

export default Finder;
