import axios from "axios";
import s from "./Finder.module.css";

const Finder = (props) => {

    if(props.users.length === 0){

      axios.get('https://social-network.samuraijs.com/api/1.0/users')

      props.setUsers([
        {userId: 1, follow: false, name: 'Greg', surname: 'Mir', age: '19', status: 'Учу React JS', location: {country: 'Ukraine', city: 'Dnipro'}, ava: ''},
        {userId: 2, follow: true, name: 'Milana', surname: 'Prudius', age: '19', status: 'Учу Medical things', location: {country: 'Ukraine', city: 'Dnipro'}, ava: ''},
        {userId: 3, follow: false, name: 'Artem', surname: 'Boyko', age: '19', status: 'Учу Boxing', location: {country: 'Ukraine', city: 'Dnipro'}, ava: ''},
        {userId: 4, follow: true, name: 'Matvey', surname: 'Kibec', age: '19', status: 'Учу calling',location: {country: 'Ukraine', city: 'Dnipro'}, ava: ''},
        {userId: 5, follow: false, name: 'Mishanya', surname: 'Kalinichenko', age: '19', status: 'Учу calling',location: {country: 'Ukraine', city: 'Dnipro'}, ava: ''},
        {userId: 6, follow: true, name: 'Oleg', surname: 'Tyapkin', age: '19', status: 'Chill...', location: {country: 'Ukraine', city: 'Dnipro'}, ava: ''},
        {userId: 7, follow: false, name: 'Ilya', surname: 'Bocmanec', age: '19', status: 'Im spinning to have some money!', location: {country: 'Ukraine', city: 'Dnipro'}, ava: ''},
        {userId: 8, follow: false, name: 'Ignatus', surname: 'Kabanus', age: '23', status: 'Прибиваю лохов', location: {country: 'Ukraine', city: 'Днепродым'}, ava: ''},
    ]);  
    }

    return (
    <div className={s.finder_wrapper}>
      <div className={s.search_block}>
        <input type="text" placeholder="Введите ФИО друга" />
        <i className="material-icons">search</i>
      </div>
      <div className={s.users}>
        {props.users.map((user) => {
          return (
            <div className={s.user_block} key={user.userId}>
              <div className={s.user_ava}>
                <span className={s.ava}></span>
              </div>
              <div className={s.user_name}>
                {user.name} {user.surname}
              </div>
              <div className={s.user_status}>
                Status: <br /> {user.status}
              </div>
              <div className={s.user_location}>
                <i className="material-icons">location_on</i>
                {"Location: " +
                  user.location.country +
                  ", " +
                  user.location.city}
              </div>
              <div className={s.user_button}><button className={user.follow ? s.followed : s.unfollowed} onClick={()=>{props.follow(user.userId);}}>{user.follow ? 'Unfollow' : 'Follow'}</button></div>
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default Finder;
