const SET_DATA = "SET-DATA";

let initialState = {
    email: null,
    login: null,
    userId: null,
}

let AuthReducer = (state = initialState, action) => {
    switch(action.type){
        case SET_DATA:{
            return {
                ...state,
                    ...action.data,
            }
        }
        default:
            return state;
    }
}

export const setData = (email, login, userId) => ({type: SET_DATA, data: {email, login, userId}})

export default AuthReducer
