import * as axios from "axios";
import s from "./Finder.module.css";
import React from "react";


class Finder extends React.Component {

  componentDidMount(){
    axios
    .get("https://social-network.samuraijs.com/api/1.0/users")
    .then((response) => {
      this.props.setUsers(response.data.items);
    });
  }

  render() {

    let pagesCount = Math.ceil(this.props.totalUsersCount / this.props.pageSize);
    let pages = [];
    for(let i=1; i<= pagesCount ; i++)
    {
      pages.push(i);
    }

    return(
    <div className={s.finder_wrapper}>
      <div className={s.search_block}>
        <input type="text" placeholder="Введите ФИО друга" />
        <i className="material-icons">search</i>
      </div>
      <div className={s.users}>
        {this.props.users.map(user => 
            <div className={s.user_block} key={user.id}>
              <div className={s.user_ava}>
                <span className={s.ava}></span>
              </div>
              <div className={s.user_name}>
                {user.name} 
              </div>
              <div className={s.user_status}>
                Status: <br /> {user.status}
              </div>
              <div className={s.user_location}>
                <i className="material-icons">location_on</i>
                {"Location: " +
                  'user.location.country' +
                  ", " +
                  'user.location.city'}
              </div>
              <div className={s.user_button}><button className={user.follow ? s.followed : s.unfollowed} onClick={()=>{this.props.follow(user.id);}}>{user.follow ? 'Unfollow' : 'Follow'}</button></div>
            </div>
        )}
      </div>
      <div className={s.page_counter_block}>
        {pages.map(p => <span className={`${s.counter} ${this.props.currentPage === p ? s.active_page : null}`}>{p}</span>)}
      </div>
    </div>
  )}
}

export default Finder;
