const UPDATE_POST_TEXT = 'UPDATE-POST-TEXT';
const ADD_POST = 'ADD-POST';

let initialState = {
    posts: [
        { id: 1, text: "Hey, im here!", likescount: 20 },
        { id: 2, text: "Hello world!", likescount: 10 },
        { id: 3, text: "Hello!", likescount: 10 },
        { id: 4, text: "world!", likescount: 10 },
      ] ,
      PostText : ''
}

const ProfileReducer = (state = initialState, action) => {
    switch(action.type){
        case UPDATE_POST_TEXT:
            {
                let stateCopy = {...state};
                stateCopy.PostText = action.newText;
                return stateCopy;
            }
        case ADD_POST:
            {
                let stateCopy = {...state};
                let post = {id: 5, text: stateCopy.PostText, likescount: 33};
                state.posts.push(post);
                state.PostText = '';
                return stateCopy;
            }

        default:
            return state;
    }
}

export const AddPostActionCreator = () => ({type: ADD_POST});
export const OnTextEnterActionCreator = (text) => ({type: UPDATE_POST_TEXT, newText: text});

export default ProfileReducer;
