import { connect } from 'react-redux';
import { setPageAC, followAC, setUsersAC, setPageSizeAC, setIsFetchingAC } from '../../../redux/reducers/finder-reducer';
import * as axios from "axios";
import React from "react";
import Finder from "./Finder";
import preloder from '../../../images/loading.gif'

class FinderContainer extends React.Component {

    componentDidMount(){
        this.props.setIsFetching(true);
      axios
      .get(`https://social-network.samuraijs.com/api/1.0/users?page=${this.props.currentPage}&count=${this.props.pageSize}`)
      .then((response) => {
        this.props.setUsers(response.data.items);
      });
    }

    onPageChanged = (pageSize) => {
      this.props.setPageSize(pageSize);
      axios
      .get(`https://social-network.samuraijs.com/api/1.0/users?count=${this.props.pageSize+5}`)
      .then((response) => {
        this.props.setUsers(response.data.items);
      });
    }

    render() { return (
      <>
        {this.props.isFetching ? <img src={preloder} /> : null}
        <Finder
          follow={this.props.follow}
          onPageChanged={this.onPageChanged}
          pageSize={this.props.pageSize}
          users={this.props.users}
        />
      </>
    );
}
  }

let mapStateToProps = (state) =>{
    return{
        users: state.FinderPage.users,
        totalUsersCount: state.FinderPage.totalUsersCount,
        pageSize: state.FinderPage.pageSize,
        currentPage: state.FinderPage.currentPage,
        isFetching: state.FinderPage.isFetching,
    }
} 

let mapDispatchToProps = (dispatch) =>{
    return{
        follow: (userId) =>{
            dispatch(followAC(userId));
        },
        setUsers: (users) =>{
            dispatch(setUsersAC(users));
        },
        setPage: (page)=>{
            dispatch(setPageAC(page));
        },
        setPageSize: (pageSize) =>{
            dispatch(setPageSizeAC(pageSize));
        },
        setIsFetching: (isFetching) =>{
            dispatch(setIsFetchingAC(isFetching));
        } 
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(FinderContainer);
