import { OnTextEnterActionCreator, AddPostActionCreator } from "../../../../redux/reducers/profile-reducer";
import MyPosts from "./MyPosts";
import StoreContext from '../../../../StoreContext';

const MyPostsConteiner = (props) => {
    let state = props.store.getState()

    let AddPost = () =>{
        let action = AddPostActionCreator();
        props.store.dispatch(action);
      }

      let onTextEnter = (text) =>{
        let action = OnTextEnterActionCreator(text);
        props.store.dispatch(action);
      }

    return (
      <StoreContext.Consumer>{
        (store) => (
          <MyPosts AddPost={AddPost} UpdatePostText={onTextEnter} posts={state.ProfilePage.posts} PostText={state.ProfilePage.PostText}/>

        )}
    </StoreContext.Consumer>
    )
}

export default MyPostsConteiner;
