import s from "./Profileinfo.module.css";
import Preloader from "../../../common/Preloader/Preloader";
import { Youtube, Instagram, Facebook, Twitter } from "react-bootstrap-icons";

const ProfileInfo = (props) => {
  let photo = () => {
    if (props.photos == null) {
      return <Preloader />;
    } else {
      return (
        <div className={s.ava}>
          <img src={props.photos.large} alt="" />
        </div>
      );
    }
  };
  let social = () => {
    {
      if (props.social != null)
        props.social.map((soc) => {
          switch (props.social.network) {
            case "youtube": {
              return (
                <a href={soc.link}>
                  {" "}
                  <Youtube />{" "}
                </a>
              );
            }
            case "instagram": {
              return (
                <a href={soc.link}>
                  {" "}
                  <Instagram />{" "}
                </a>
              );
            }
            case "facebook": {
              return (
                <a href={soc.link}>
                  {" "}
                  <Facebook />{" "}
                </a>
              );
            }
            case "twitter": {
              return (
                <a href={soc.link}>
                  {" "}
                  <Twitter />{" "}
                </a>
              );
            }
            default:
              return null;
          }
        });
    }
  };
  return (
    <div className={s.profile_wrapper}>
      {photo()}
      <div className="status">
        <p>{props.aboutMe}</p>
        <div className={s.user_links}>{social()}</div>
        <div className={s.friends_block}>
          <h5>Friends:</h5>
          <div className={s.my_friends}>
            {/* {props.friends.map((friend) => (
          <div className={s.my_friend} key={friend.id}>
            <div className={s.friend_ava}>
            <i className="material-icons">account_circle</i>
            </div>
            <div className={s.friend_name}>{friend.name}</div>
          </div>
        ))} */}
          </div>
        </div>
      </div>
    </div>
  );
};
export default ProfileInfo;
