import s from "./Profileinfo.module.css"
import Preloader from '../../../common/Preloader/Preloader'

const ProfileInfo = (props) =>{
  let photo = () => {
    if (props.photos == null){
      return <Preloader />;
    }
    else{
      return <div className={s.ava}>
      <img src={props.photos.large} alt="" />
      </div>
    }
  }
  return (
  <div className={s.profile_wrapper}>
    {photo()}
    <div className="status">
      <p>{props.aboutMe}</p>
      <div className={s.user_links}>
          <span className={s.twitter}>
          <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" class="bi bi-twitter" viewBox="0 0 16 16">
            <path d="M5.026 15c6.038 0 9.341-5.003 9.341-9.334 0-.14 0-.282-.006-.422A6.685 6.685 0 0 0 16 3.542a6.658 6.658 0 0 1-1.889.518 3.301 3.301 0 0 0 1.447-1.817 6.533 6.533 0 0 1-2.087.793A3.286 3.286 0 0 0 7.875 6.03a9.325 9.325 0 0 1-6.767-3.429 3.289 3.289 0 0 0 1.018 4.382A3.323 3.323 0 0 1 .64 6.575v.045a3.288 3.288 0 0 0 2.632 3.218 3.203 3.203 0 0 1-.865.115 3.23 3.23 0 0 1-.614-.057 3.283 3.283 0 0 0 3.067 2.277A6.588 6.588 0 0 1 .78 13.58a6.32 6.32 0 0 1-.78-.045A9.344 9.344 0 0 0 5.026 15z"/>
          </svg>
          </span>
      </div>
    </div>
    <div className={s.friends_block}>
      <h5>Friends:</h5>
      <div className={s.my_friends}>
        {/* {props.friends.map((friend) => (
          <div className={s.my_friend} key={friend.id}>
            <div className={s.friend_ava}>
            <i className="material-icons">account_circle</i>
            </div>
            <div className={s.friend_name}>{friend.name}</div>
          </div>
        ))} */}
      </div>
    </div>
  </div>
);
}

export default ProfileInfo;
