import ProfileReducer from "./profile-reducer";
import DialogsReducer from "./dialogs-reducer";


let store = {

   _state: {

    ProfilePage: {
      posts: [
        { id: 1, text: "Hey, im here!", likescount: 20 },
        { id: 2, text: "Hello world!", likescount: 10 },
        { id: 3, text: "Hello!", likescount: 10 },
        { id: 4, text: "world!", likescount: 10 },
      ] ,
      EnteredText : 'i got this'
    },
  
    DialogsPage: {
      messages: [
        { id: 1, text: "Hi!" },
        { id: 2, text: "Yo!" },
        { id: 3, text: "Lol!" },
        { id: 4, text: "Kek" },
        { id: 5, text: "Chevurek!" },
      ],

      NewMessageText: '',

      dialogs: [
        { id: 1, name: "Alex" },
        { id: 2, name: "Vasya" },
        { id: 3, name: "Petya" },
        { id: 4, name: "Kolya" },
        { id: 5, name: "Ivan" },
      ],
    },
    
    NavMenu: {
      links: [
        {id: 1, href:'/Profile', name:'Profile', icon:'face'},
        {id: 2, href:'/Dialogs', name:'Dialogs', icon:'mail'},
        {id: 3, href:'/Friends', name:'Friends', icon:'audiotrack'}
      ],
      logo: ['Logo'],
    },
  
    FriendsPage:{
      friends: [
        { id: 1, name: "Alex", photo: "" },
        { id: 2, name: "Vasya", photo: ""},
        { id: 3, name: "Petya", photo: ""},
        { id: 4, name: "Kolya", photo: "" },
        { id: 5, name: "Ivan", photo: "" }
      ]
    },
  },
  _callSubscriber() {
    console.log('State changed');
  },

  getState() {
    return this._state;
  },
  subscribe(observer){
    this._callSubscriber = observer;
  },

  dispatch(action){ // {type: 'ADD-POST' any: '' }
    this._state.ProfilePage = ProfileReducer(this._state.ProfilePage, action);
    this._state.DialogsPage = DialogsReducer(this._state.DialogsPage, action);

    this._callSubscriber(this._state);
  }

}

window.store = store;

export default store;
