import s from './Finder.module.css';

const Finder = (props) => {
    return(
    <div className={s.finder_wrapper}>
        <div className={s.search_block}>
        <input type="text" placeholder='Введите ФИО друга' />
        <i class="material-icons">search</i>
        </div>
        <div className={s.users}>
            {props.users.map(user =>{
                let follow_button_function = () => {
                    if(user.follow){
                        return (
                            <button className={s.followed}>Unfollow</button>
                        )
                    }
                    else{
                        return(
                            <button className={s.unfollowed}>Follow</button>
                        )
                    }
                }
                return (
                  <div className={s.user_block} key={user.userId}>
                    <div className={s.user_ava}>
                      <span className={s.ava}></span>
                    </div>
                    <div className={s.user_name}>{user.name} {user.surname}</div>
                    <div className={s.user_status}>{'Status:' + '\n' + user.status}</div>
                    <div className={s.user_location}>
                      {user.location.country} {user.location.city}
                    </div>
                    <div className={s.user_button}>{follow_button_function()}</div>
                  </div>
                );} )}

        </div>
    </div>
    )
}

export default Finder;
