const SET_USER_INFO = "SET-USER-INFO";

let initialState = {
    email: null,
    login: null,
    userId: null,
    isAuth: false,
}

let AuthReducer = (state = initialState, action) => {
    switch(action.type){
        case SET_DATA:{
            return {
                ...state,
                    ...action.data,
                    isAuth: true,
            }
        }
        case SET_USER_INFO:{
           return {
                ...state,
                    ...info,
           } 
        }

        default:
            return state;
    }
}

export const setData = (data) => ({type: SET_DATA, data: {...data}});
export const setUserInfo = (info) => ({type: SET_USER_INFO, info});

