import Finder from "./Finder";
import { connect } from 'react-redux';
import { followAC, setUsersAC } from '../../../redux/reducers/finder-reducer';

let mapStateToProps = (state) =>{
    return{
        users: state.FinderPage.users,
        totalUsersCount: state.FinderPage.totalUsersCount,
        pagesCount: state.FinderPage.pageSize,
    }
} 

let mapDispatchToProps = (dispatch) =>{
    return{
        follow: (userId) =>{
            dispatch(followAC(userId));
        },
        setUsers: (users) =>{
            dispatch(setUsersAC(users));
        }
    }
}

let FinderContainer = connect(mapStateToProps, mapDispatchToProps)(Finder);

export default FinderContainer;
