import s from "./Dialogs.module.css";
import Dialog from "./DialogItem/DialogItem";
import Message from "./MessageItem/MessageItem";

const Dialogs = (props) => {
  let DialogsElements = props.data.dialogs.map(dialog => (<Dialog key={dialog.id} name={dialog.name} id={dialog.id}/>) )
  let Messages = props.data.messages.map( message => (<Message key={message.id} text={message.text} id={message.id}/>) )

  return (
    <div className={s.dialogs_wrapper}>
      <div className={s.dialogs}>
        <h5>Dialogs</h5>
        {DialogsElements}
      </div>
      <div className={s.messages}>
        {Messages}
        <div className={s.post_block}>
          <textarea ref={AddPostText} name="" id="" cols="30" rows="10" placeholder="Что у вас нового?"></textarea>
          <button className={s.add_post_button} onClick={addPost}>Добавить пост</button>
          <button className={s.delete_button}>Удалить</button>
        </div>
      </div>
    </div>
  );
};

export default Dialogs;
