import { connect } from 'react-redux';
import { setPage, follow, setUsers, setPageSize, setIsFetching } from '../../../redux/reducers/finder-reducer';
import * as axios from "axios";
import React from "react";
import Finder from "./Finder";
import { getUsers } from '../../../API/api';

class FinderContainer extends React.Component {

    componentDidMount(){
        this.props.setIsFetching(true);
      getUsers().then((response) => {
        this.props.setIsFetching(false);
        this.props.setUsers(response.items);
      });
    }

    onPageChanged = (pageSize) => {
        this.props.setIsFetching(true);
      this.props.setPageSize(pageSize);
      axios
      .get(`https://social-network.samuraijs.com/api/1.0/users?count=${this.props.pageSize+5}`, {withCredentials: true})
      .then((response) => {
        this.props.setUsers(response.data.items);
        this.props.setIsFetching(false);
      });
    }

    render() {
      return (
      <>
          <Finder
            follow={this.props.follow}
            onPageChanged={this.onPageChanged}
            pageSize={this.props.pageSize}
            users={this.props.users}
            isFetching={this.props.isFetching}
          />
      </>
    );
}
  }

let mapStateToProps = (state) =>{
    return{
        users: state.FinderPage.users,
        totalUsersCount: state.FinderPage.totalUsersCount,
        pageSize: state.FinderPage.pageSize,
        currentPage: state.FinderPage.currentPage,
        isFetching: state.FinderPage.isFetching,
    }
} 


export default connect(mapStateToProps, {follow, setUsers, setPage,setPageSize, setIsFetching})(FinderContainer);
